import { Icategory } from 'src/app/Models/icategory';
import { Iproduct } from './../../Models/iproduct';
import { Component } from '@angular/core';

@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css']
})
export class ProductsComponent {
  Product:Iproduct[]
  Category:Icategory[]
constructor(){
  this.Product = 
  [
    {id:12,name:'Labtop',price:120,quantity:3,imgURL:'https://fakeimg.pl/350x200/?text=Hello',Category:1},
    {id:12,name:'Phone 12',price:120,quantity:3,imgURL:'https://fakeimg.pl/350x200/?text=Hello',Category:1},
    {id:12,name:'Suamsong 13',price:120,quantity:3,imgURL:'https://fakeimg.pl/350x200/?text=Hello',Category:1},
    {id:12,name:'Hard 123',price:120,quantity:3,imgURL:"https://fakeimg.pl/200x100/?retina=1&text=こんにちは&font=noto",Category:1},
    {id:12,name:'Labtop',price:120,quantity:3,imgURL:'https://fakeimg.pl/350x200/?text=Hello',Category:1},
    {id:12,name:'Labtop',price:120,quantity:3,imgURL:'https://fakeimg.pl/350x200/?text=Hello',Category:1},

  ]
  this.Category=[
    {id:1,name:'Mobile'},
    {id:1,name:'Device Desktop'},
    {id:1,name:'Samsung'}
  ]

}
}
