import { Icategory } from 'src/app/Models/icategory';
import { Iproduct } from './../../Models/iproduct';
import { Component } from '@angular/core';

@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css']
})
export class ProductsComponent {
  Product:Iproduct[]
  Category:Icategory[]
   totalprice:number=0;
constructor(){
  this.Product = 
  [
    {id:1,name:'Labtop',price:120,quantity:0,imgURL:'https://fakeimg.pl/350x200/?text=Hello',Category:1},
    {id:2,name:'Phone 12',price:120,quantity:3,imgURL:'https://fakeimg.pl/350x200/?text=Hello',Category:1},
    {id:3,name:'Suamsong 13',price:120,quantity:3,imgURL:'https://fakeimg.pl/350x200/?text=Hello',Category:1},
    {id:4,name:'Hard 123',price:120,quantity:3,imgURL:"https://fakeimg.pl/200x100/?retina=1&text=こんにちは&font=noto",Category:1},
    {id:5,name:'Labtop',price:120,quantity:3,imgURL:'https://fakeimg.pl/350x200/?text=Hello',Category:1},
    {id:6,name:'Labtop',price:120,quantity:3,imgURL:'https://fakeimg.pl/350x200/?text=Hello',Category:1},
    {id:7,name:'Labtop',price:120,quantity:3,imgURL:'https://fakeimg.pl/350x200/?text=Hello',Category:1},
    {id:8,name:'Labtop',price:120,quantity:1,imgURL:'https://fakeimg.pl/350x200/?text=Hello',Category:1},
    {id:9,name:'Labtop',price:120,quantity:0,imgURL:'https://fakeimg.pl/350x200/?text=Hello',Category:1},
  ]
  this.Category=[
    {id:1,name:'Mobile'},
    {id:1,name:'Device Desktop'},
    {id:1,name:'Samsung'}
  ]

}

buyItem(id: number, quantity: any) {
  const productIndex = this.Product.findIndex(product => product.id === id);
  if (productIndex !== -1) {
    if (quantity > 0 && quantity <= this.Product[productIndex].quantity) {
      // Deduct the selected quantity from the stock
      this.Product[productIndex].quantity -= quantity;
      this.totalprice = this.Product[productIndex].price * quantity;
      console.log(`Item with ID ${id} and quantity ${quantity} has been purchased.`);
    } else {
      console.log(`Invalid quantity for item with ID ${id}.`);
    }
  } else {
    console.log(`Item with ID ${id} not found.`);
  }
}

}
