import { Icategory } from "src/app/Models/icategory";
import { Iproduct } from "./../../Models/iproduct";
import { Component, Input, OnChanges } from "@angular/core";

@Component({
  selector: "app-products",
  templateUrl: "./products.component.html",
  styleUrls: ["./products.component.css"],
})
export class ProductsComponent implements OnChanges {
  Product: Iproduct[];
  ProductOfCaegory: Iproduct[] = [];
  totalprice: number = 0;
  @Input() selectId: number = 0;
  // @Input() ProductOfCategory: any[] = []; // Input property to receive products of the selected category
  constructor() {
    this.Product = [
      {
        id: 1,
        name: "Labtop",
        price: 120,
        quantity: 0,
        imgURL: "https://fakeimg.pl/350x200/?text=Hello",
        Category: 3,
      },
      {
        id: 2,
        name: "Phone 12",
        price: 120,
        quantity: 3,
        imgURL: "https://fakeimg.pl/350x200/?text=Hello",
        Category: 1,
      },
      {
        id: 3,
        name: "Suamsong 13",
        price: 120,
        quantity: 3,
        imgURL: "https://fakeimg.pl/350x200/?text=Hello",
        Category: 1,
      },
      {
        id: 4,
        name: "Hard 123",
        price: 120,
        quantity: 3,
        imgURL:
          "https://fakeimg.pl/200x100/?retina=1&text=こんにちは&font=noto",
        Category: 2,
      },
      {
        id: 5,
        name: "Labtop",
        price: 120,
        quantity: 3,
        imgURL: "https://fakeimg.pl/350x200/?text=Hello",
        Category: 2,
      },
      {
        id: 6,
        name: "Labtop",
        price: 120,
        quantity: 3,
        imgURL: "https://fakeimg.pl/350x200/?text=Hello",
        Category: 1,
      },
      {
        id: 7,
        name: "Labtop",
        price: 120,
        quantity: 3,
        imgURL: "https://fakeimg.pl/350x200/?text=Hello",
        Category: 1,
      },
      {
        id: 8,
        name: "Labtop",
        price: 120,
        quantity: 2,
        imgURL: "https://fakeimg.pl/350x200/?text=Hello",
        Category: 1,
      },
      {
        id: 9,
        name: "Labtop",
        price: 120,
        quantity: 0,
        imgURL: "https://fakeimg.pl/350x200/?text=Hello",
        Category: 3,
      },
    ];

  }
  ngOnChanges(): void {
    this.FilterProductByCategory()
  }

  buyItem(id: number, quantity: any) {
    const productIndex = this.Product.findIndex((product) => product.id === id);
    if (productIndex !== -1) {
      if (quantity > 0 && quantity <= this.Product[productIndex].quantity) {
        // Deduct the selected quantity from the stock
        this.Product[productIndex].quantity -= quantity;
        this.totalprice = this.Product[productIndex].price * quantity;
        console.log(
          `Item with ID ${id} and quantity ${quantity} has been purchased.`
        );
      } else {
        console.log(`Invalid quantity for item with ID ${id}.`);
      }
    } else {
      console.log(`Item with ID ${id} not found.`);
    }
  }

  private FilterProductByCategory() {
    this.ProductOfCaegory = this.Product.filter(
      (item) => (item.Category = this.selectId)
    );
    // if (this.selectId == 0) 
    //   this.ProductOfCaegory = this.Product;

    // else
    //   console.log('Error')

  }
}
