<h3>Products List</h3>
<table class="table mt-5">
    <thead>
        <th>Id</th>
        <th>Name</th>
        <th>Img</th>
        <th>Price</th>
        <th>Stock</th>
        <th>No.items</th>
        <th>Buy</th>
    </thead>
    <tbody >
                <tr *ngFor="let prod of ProductOfCaegory let i = index"
                 [class.d-none]="prod?.quantity == 0"
                 [style.background-color]="(prod.quantity == 1) ? 'yellow' : 'white'">
            <td>{{i+1}}</td>
            <td>{{prod?.name}}</td>
            <td><img [src]="prod?.imgURL" alt="" width="100px"></td>
            <td>{{prod?.price | currency}}</td>
            <td>{{prod?.quantity}}</td>
            <td>
                <div>
                    <input type="number" #item  name="quantity"  min="1" max="{{prod?.quantity}}">
                </div>
            </td>
            <td><button class="btn btn-success" (click)="buyItem(prod.id,item.value,prod)">Buy</button></td>
        </tr>

    </tbody>
</table>
