import { Component, OnChanges, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Iorders } from 'src/app/Models/iorders';
import { Iproduct } from 'src/app/Models/iproduct';
import { OrdersService } from 'src/app/Services/orders.service';

@Component({
  selector: 'app-orders-list',
  templateUrl: './orders-list.component.html',
  styleUrls: ['./orders-list.component.css']
})
export class OrdersListComponent implements OnInit ,OnChanges {
  orders:Iorders[] | any
  constructor(private apiorder:OrdersService,
    private routes:Router
  ){

  }
  ngOnInit(): void {
    let User = localStorage.getItem('user')
  if(User){
   let user_id = JSON.parse(User) 
     console.log(user_id.id)
      this.apiorder.getordersbyuserid(user_id.id).subscribe((prod)=>{
      this.orders = prod
  })
    }
  }
  ngOnChanges(): void {

  }
  calculateTotalPrice(): number {
    let total = 0;
    if (this.orders) {
      for (let order of this.orders) {
        total += order.price;
      }
    }
    return total;
  }


  purchaseItems(): void {
    if (confirm("Are you sure you want to purchase these items?")) {
      for (let order of this.orders) {
        // Subtract the purchased quantity from the available quantity
        order.quantity -= order.countitem; // Assuming you have a property named countitem to store the selected quantity

        // Update the product quantity in your database or data source
        this.apiorder.updateProductQuantity(order.id, order.quantity).subscribe(() => {
          console.log(`Quantity updated for product with ID: ${order.id}`);
        }, (error) => {
          console.error("Error updating quantity:", error);
          // You can handle errors here, such as displaying an error message to the user
        });
      }
    }
  }


  openproductId(id:number){
    this.routes.navigate(['/products',id])
    }

}
